	function trimSpace ($array)
	{
		$temp = Array();
		for ($i = 0; $i < count($array); $i++)
				$temp[$i] = trim($array[$i]);

		return $temp;
		
	}

	function getMaxCode($scode)  { 
	
		$iNewid = 0;
		$sqlstr = "SELECT MAX(SeqNo) CNT FROM tb_board where BoardId = '$scode'"; 
	
		$result = mysql_query($sqlstr);
		$row = mysql_fetch_array($result);
	
		$iNewid = $row["CNT"] + 1;

		return $iNewid;
	
	}

